
import open from './modules/open'
import hit from './modules/hit'
import stay from "./modules/stay"

export default {
  deck: function(deck) {
    deck.blackjack = {}
    open.deck(deck);
    hit.deck(deck);
    //stayは要らない
  },
  card: function(card) {
    card.blackjack = {};
    open.card(card);
    hit.card(card);
    stay.card(card)
  }
}
