
import getFontSize from 'deck-of-cards/lib/fontSize'
var fontSize

export default {
  card: function (card) {
    var $el = card.$el

    fontSize = getFontSize()

    card.blackjack.stay = function (i) {
        if (i === 1) {card.animateTo({
        delay: 0,
        duration: 250,
        x: Math.round((1 - 2.05) * 70 * fontSize / 16) - 200,
        y: Math.round(-150 * fontSize / 16),
        rot: 0,

        onStart: function () {
          $el.style.zIndex = 52
        },
        onComplete: function () {
        }
      });      
    }
    else card.setSide("front");
    }
  }
}
